    public function getSourceContext()
    {
        return new Source("<!DOCTYPE html>
<html>
    <head>
        <meta charset=\"UTF-8\">
        <title>{% block title %}Mon Restaurant{% endblock %}</title>
        <link rel=\"stylesheet\" href=\"https://stackpath.bootstrapcdn.com/bootstrap/4.4.1/css/bootstrap.min.css\" integrity=\"sha384-Vkoo8x4CGsO3+Hhxv8T/Q5PaXtkKtu6ug5TOeNV6gBiFeWPGFN9MuhOf23Q9Ifjh\" crossorigin=\"anonymous\">
        <link rel=\"stylesheet\" href=\"{{asset('assets/css/master.css')}}\">
        {% block stylesheets %}{% endblock %}
    </head>
    <body>
      <nav class=\"navbar navbar-expand-lg navbar-dark bg-dark\">
          <a class=\"navbar-brand\" href=\"{{path('home')}}\">Accueil</a>
          <button class=\"navbar-toggler\" type=\"button\" data-toggle=\"collapse\" data-target=\"#navbarNav\" aria-controls=\"navbarNav\" aria-expanded=\"false\" aria-label=\"Toggle navigation\">
            <span class=\"navbar-toggler-icon\"></span>
          </button>

          <div class=\"collapse navbar-collapse\" id=\"navbarNav\">
            <ul class=\"navbar-nav mr-auto\">
              <li class=\"nav-item\">
                <a class=\"nav-link\" href=\"{{path('plat_index')}}\">Les plats</a>
              </li>
              <li class=\"nav-item\">
                <a class=\"nav-link\" href=\"#\">L'équipe</a>
              </li>
              <li class=\"nav-item\">
                <a class=\"nav-link\" href=\"{{path('home_contact')}}\">Contacts</a>
              </li>
              {% if is_granted('ROLE_SUPER_ADMIN') %}
                <li class=\"nav-item\">
                  <a class=\"nav-link\" href=\"{{ path('admin_index') }}\">Admin</a>
                </li>
              {% endif %}
            </ul>
            <ul class=\"navbar-nav\">
              {% if not app.user %}
              <li>
                <a class=\"nav-link\" href=\"{{ path('app_register') }}\"><i>Enregistrement</i></a>
              </li>
              <li class=\"nav-item\">
                <a class=\"nav-link\" href=\"{{ path('app_login') }}\"><i>Connexion</i></a>
              </li>
              {% else %}
              <li class=\"nav-item\">
                <a class=\"nav-link\" href=\"{{ path('app_logout') }}\">
                  {{ app.user.username }} / Déconnexion
                </a>
              </li>
              {% endif %}
            </ul>
          </div>
      </nav>
    {% block body %}{% endblock %}
    {% block javascripts %}{% endblock %}
    <script src=\"https://code.jquery.com/jquery-3.4.1.slim.min.js\" integrity=\"sha384-J6qa4849blE2+poT4WnyKhv5vZF5SrPo0iEjwBvKU7imGFAV0wwj1yYfoRSJoZ+n\" crossorigin=\"anonymous\"></script>
